package com.java.IntialConcepts;

public class PrintlnConditions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// stage--1)literals print
		System.out.println("literals print");
		System.out.println("Hello World");
		System.out.println(90);		
		System.out.println(7.6);		
		System.out.println('f');		
		System.out.println('G');		
		System.out.println(true);

		// stage-2)Mathematical operations
		System.out.println("\nMathematical operations");
		System.out.println(90+20);
		System.out.println(90-10);
		System.out.println(90*3);
		System.out.println(90/5);
		
		//stage-3) concatination
		System.out.println("\nconcatination");
		System.out.println("I Love India"+" Also i love fredoom");
		System.out.println("Mom"+20+10.6);
		System.out.println(20+4+"vichar");
		System.out.println("mind"+(2+3));
		System.out.println("string"+'j');
		System.out.println("high"+true);
		System.out.println("we see"+null);
		
	}

}
